mapper.update:
update CHAT_INVITE
set APPROVAL_YN = 'Y'
where CHAT_ROOM_CODE = #{roomCode} and RECIPIENT_ID = #{id}

mapper.select:
select PARTICIPANT_ID
from CHAT_USER
where CHAT_ROOM_CODE = #{roomCode}

mapper.update:
update CHAT_AVAILABILITY
set RECEIVING_STATUS = 'Y'
where PARTICIPANT_ID = #{id} and CHAT_ID = #{chatId}
