mapper.insert:
insert into hotel
values(
	SEQ_HID.nextval, #{entId}, #{hotelName}, #{hotelPhone}, #{hotelAddress}, #{wifi}, #{breakfast}, #{amenity}, #{park}, #{swim}, #{fitness}, default, default, #{star}, #{hotelInfo}, #{hotelIntro}, default, #{hotelGeoX}, #{hotelGeoY}
)

mapper.update:
update hotel
set
	hotel_name=#{hotelName}, hotel_phone=#{hotelPhone}, hotel_address=#{hotelAddress},
	wifi=#{wifi}, breakfast=#{breakfast}, amenity=#{amenity}, park=#{park}, swim=#{swim}, fitness=#{fitness},
	star=#{star}, hotel_info=#{hotelInfo}, hotel_intro=#{hotelIntro}, hotel_geo_x=#{hotelGeoX}, hotel_geo_y=#{hotelGeoY}
where hotel_id=#{hotelId}

mapper.delete:
delete from attached_file where file_modify_name in
#{item}
and file_type='H'

mapper.update:
update room
set
	room_name=#{roomName}, max_accept=#{maxAccept}, room_price=#{roomPrice},
	checkin_time=#{checkinTime}, checkout_time=#{checkoutTime},
	total_number=#{totalNumber}, room_info=#{roomInfo}, room_intro=#{roomIntro}
where room_id=#{roomId}

update room
set room_status='N'
where room_id=#{roomId}

mapper.select:
select *
from room
where room_id=#{roomId}

select count(*)
from like_hotel
where id=#{id} and hotel_id=#{hotelId}

select *
from hotel
where ent_id=#{id}

select count(*) from hotel join hotel_img using(hotel_id) join attached_file using(file_id) where hotel_status='Y' and file_thumbnail='Y'
and min_price(hotel_id)>=#{minPrice} and min_price(hotel_id)<=#{maxPrice}
and DISTNACE_WGS84(round(hotel_geo_x, 10), round(hotel_geo_y, 10), #{geoX}, #{geoY})<#{maxDistance}
and star in #{item}
and wifi='Y'
and breakfast='Y'
and amenity='Y'
and park='Y'
and swim='Y'
and fitness='Y'

mapper.update:
update review
set business_reply=#{businessReply}
where review_id=#{reviewId}
