mapper.select:
select count(*)
from member
where USER_ID=#{id}

  		select count(*)
	  	from member
	  	where email=#{email}

  		select count(*)
	  	from member
	  	where id=#{id}

  		select count(*)
	  	from member
	  	where nickname=#{nickName}

  		select pwd
	  	from member
	  	where id=#{id}

mapper.update:
update member
set pwd=#{pwd} , NICKNAME=#{nickName}, phone=#{phone}, address=#{address}
WHERE ID = #{id}

update member
set report = report + 1
where id = #{id}

update member
set status = 'S'
where id = #{id}

update member
set pwd=#{pwd} 
WHERE ID = #{id}

mapper.select:
select id
from member
where email=#{email}

 select count(*)
 from RESERVATION join ROOM using(room_id) JOIN HOTEL_IMG using(hotel_Id) JOIN ATTACHED_FILE using(FILE_Id) JOIN HOTEL using(hotel_Id)
	where member_id = #{id} and RESERVATION_STATUS = 'Y' and FILE_THUMBNAIL = 'Y'

  select *
from RESERVATION join ROOM using(room_id) JOIN HOTEL_IMG using(hotel_Id) JOIN ATTACHED_FILE using(FILE_Id) JOIN HOTEL using(hotel_Id)
where member_id = #{id} and RESERVATION_STATUS = 'Y' and FILE_THUMBNAIL = 'Y'

mapper.update:
update QA
set QA_STATUS='N'
WHERE QA_ID = #{id}

update RESERVATION
set RESERVATION_STATUS='N'
WHERE RESERVATION_ID = #{id}

mapper.select:
 select count(*)
 from hotel join LIKE_HOTEL using(hotel_id)
	where id = #{id}  and HOTEL_STATUS = 'Y'

 select count(*)
 from PARTY_PARTICIPATE
	where member_id = #{id}

select *
from board join board_img using (board_id) join ATTACHED_FILE using(file_id)
where BOARD_WRITER = #{id} and BOARD_STATUS = 'Y' and FILE_THUMBNAIL='Y'
